import Vue from 'vue'
import App from './App.vue'
import router from './router'
import store from './store'
import axios from 'axios'
import VueAxios from 'vue-axios'
import reset from './assets/css/reset.css'
import  './assets/css/iconfont/iconfont.css'
import  './assets/css/iconfont/iconfont.js'
import global_msg from './global/global.js' //将global.js引入
import ElementUI from 'element-ui';
import 'element-ui/lib/theme-chalk/index.css';

Vue.prototype.$global_msg = global_msg;//使用$global_msg作为global_msg的原型名
Vue.config.productionTip = false
Vue.use(ElementUI);
Vue.use( VueAxios, axios)

//使用axios
Vue.prototype.$axios = axios;
// /a/b : /api/a/b => /a/b
// axios.defaults.baseURL = '/api';

//超时设置
axios.defaults.timeout = 8000;

axios.defaults.headers = {
  'Content-Type': 'application/x-www-form-urlencoded',
  Accept: 'application/json',
  //'Content-Type': 'multipart/form-data'
};

// 请求拦截器：自动携带 token
axios.interceptors.request.use(config => {
  const token = sessionStorage.getItem('satoken'); // 注意 key 是 'satoken'
  if (token) {
    config.headers['satoken'] = token; // ← 关键！Sa-Token 默认读这个 header
  }
  return config;
}, error => {
  return Promise.reject(error);
});

new Vue({
  router,
  store,
  render: h => h(App)
}).$mount('#app')
